import React, { useCallback, useState } from 'react';
import Head from 'next/head';
import { AppLayout } from '../components';
import { Form, Input, Typography } from 'antd';

type FieldType = {
  userId: string;
  userName: string;
  password: string;
  confirmPassword: string;
};

const Signup = () => {
  const [form] = Form.useForm();
  const { password, confirmPassword }: FieldType = form.getFieldsValue();
  const [passwordError, setPasswordError] = useState<boolean>(false);

  const onSubmit = () => {
    const { userId, userName, password, confirmPassword }: FieldType =
      form.getFieldsValue();

    if (confirmPassword !== password) {
      return setPasswordError(true);
    }
    console.log(userId, userName, password);
  };

  const handlePasswordCheck = useCallback(() => {
    console.log(password, confirmPassword);
    if (password !== confirmPassword) {
      setPasswordError(true);
    }
  }, [password, confirmPassword]);

  return (
    <AppLayout>
      <Head>
        <title>Sign Up | Next Tweet</title>
      </Head>
      <Form
        form={form}
        onFinish={onSubmit}>
        <Form.Item<FieldType>
          label='ID'
          name='userId'>
          <Input />
        </Form.Item>
        <Form.Item<FieldType>
          label='Nickname'
          name='userName'>
          <Input />
        </Form.Item>
        <Form.Item<FieldType>
          label='Password'
          name='password'>
          <Input type='password' />
        </Form.Item>
        <Form.Item<FieldType>
          label='Confirm Password'
          name='confirmPassword'>
          <div>
            <Input
              type='password'
              onChange={handlePasswordCheck}
            />
            {passwordError && (
              <Typography color='red'>Password not match</Typography>
            )}
          </div>
        </Form.Item>
      </Form>
    </AppLayout>
  );
};

export default Signup;
